app = Flask(__name__)


# Helper function to get random card for practice
def get_random_card():
    return random.choice(vocab_list)

# Helper function to generate multiple-choice options
def generate_options(correct_word):
    options = [correct_word]
    while len(options) < 5:
        random_word = random.choice(vocab_list)['word']
        if random_word not in options:
            options.append(random_word)
    random.shuffle(options)
    return options

@app.route('/')
def index():
    return render_template('index.html', vocab_list=vocab_list)

@app.route('/practice')
def practice():
    card = get_random_card()
    options = generate_options(card['word'])
    return render_template('practice.html', card=card, options=options)

@app.route('/check_answer', methods=['POST'])
def check_answer():
    data = request.get_json()
    card_id = data['card_id']
    user_answer = data['answer'].strip().lower()
    card = next((item for item in vocab_list if item['id'] == card_id), None)
    
    if card:
        correct = card['word'].strip().lower() == user_answer
        return jsonify({'correct': correct, 'correct_word': card['word']})
    return jsonify({'correct': False, 'correct_word': ''})

if __name__ == '__main__':
    app.run(debug=True)
